import React from "react";
import { Dots, Public } from "../../svg";
import Message from "../LoadingError/Error";
import Loading from "../LoadingError/Loading";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import Moment from "react-moment";
const ReportMain = () => {
  const reportList = useSelector((state) => state.reportList);
  const { loading, error, reports } = reportList;
  console.log(reports);
  return (
    <section className="content-main">
      <div className="content-header">
        <h2 className="content-title">Reports</h2>
      </div>

      <div className=" mb-4 shadow-sm">
        <header className="card-header bg-white">
          <div className="row gx-3 py-3">
            <div className="col-lg-4 col-md-6 me-auto">
              <input
                type="text"
                placeholder="Search..."
                className="form-control p-2"
              />
            </div>
            <div className="col-lg-2 col-6 col-md-3">
              <select className="form-select">
                <option>Status</option>
                <option>Active</option>
                <option>Disabled</option>
                <option>Show all</option>
              </select>
            </div>
            <div className="col-lg-2 col-6 col-md-3">
              <select className="form-select">
                <option>Show 20</option>
                <option>Show 30</option>
                <option>Show 40</option>
              </select>
            </div>
          </div>
        </header>
        <div className="card-body">
          <div
            className="table-responsive"
            style={{
              justifyContent: "center",
              display: "grid",
              flexDirection: "column",
            }}
          >
            {loading ? (
              <Loading />
            ) : error ? (
              <Message variant="alert-danger">{error}</Message>
            ) : (
              <>
                {reports.length === 0 ? (
                  <>
                    {" "}
                    <div className="No_results">
                      <p>No reports to show</p>
                    </div>
                  </>
                ) : (
                  <>
                    {reports.map((report, i) => (
                      <div className="profile_card" style={{ width: "712px"}}>
                        <div>
                          <p></p>
                          <p style={{ display: "flex" }}>
                            <Link
                              to={`/profile/${report.userRef._id}`}
                              className="hover6"
                              style={{ color:"#000"}}
                            >
                              {report.userRef.first_name}{" "}
                              {report.userRef.last_name}{" "}
                            </Link>
                            {report.postRef && !report.commentRef && (
                              <>
                                <span style={{ fontWeight: "300" }}>
                                  reported this post for
                                </span>
                              </>
                            )}
                            {report.commentRef && (
                              <>
                                {" "}
                                <span style={{ fontWeight: "300" }}>
                                  {" "}
                                  reported this comment on
                                </span>
                                <div
                                  // to={`/group/${idGroup}?post_id=${report.postRef._id}&comment_id=${report.commentRef._id}`}
                                  className="hover6"
                                  // onClick={() =>
                                  //   handleLinkClick(
                                  //     `/group/${idGroup}?post_id=${report.postRef._id}&comment_id=${report.commentRef._id}`
                                  //   )
                                  // }
                                >
                                  {" "}
                                  {report.userRef.first_name}'s Post
                                </div>
                                <span style={{ fontWeight: "300" }}> for</span>
                              </>
                            )}
                            <span style={{ fontWeight: "400" }}>
                              {" "}
                              "{report.type}".
                            </span>
                          </p>
                        </div>
                        <div className="mmenu_splitter"></div>
                        {report.postRef && !report.commentRef && (
                          <>
                            <div className="posts">
                              <div className="post">
                                <div
                                  className="post_header"
                                  id={`post-${report.postRef._id}`}
                                >
                                  <div className="post_header_left">
                                    <Link
                                      to={`/profile/${report.postRef.user._id}`}
                                    >
                                      <img
                                        src={report.postRef.user.picture}
                                        alt=""
                                      />
                                    </Link>

                                    <div className="header_col">
                                      <div className="post_profile_name">
                                        <>
                                          <Link
                                            to={`/profile/${report.postRef.user._id}`}
                                            className="hover6"
                                          >
                                            {report.postRef.user.first_name}{" "}
                                            {report.postRef.user.last_name}
                                          </Link>
                                        </>

                                        <div className="updated_p">
                                          {report.postRef.type ==
                                            "profilePicture" &&
                                            `updated ${
                                              report.postRef.user.gender ===
                                              "male"
                                                ? "his"
                                                : "her"
                                            } profile picture`}
                                          {report.postRef.type ==
                                            "coverPicture" &&
                                            `updated ${
                                              report.postRef.user.gender ===
                                              "male"
                                                ? "his"
                                                : "her"
                                            } cover picture`}
                                          {report.postRef.type ==
                                            "coverPictureGroup" &&
                                            `updated the group cover photo.`}
                                        </div>
                                      </div>
                                      <div className="post_profile_privacy_date">
                                        <Moment fromNow interval={30}>
                                          {report.postRef.createdAt}
                                        </Moment>
                                        . <Public color="#828387" />
                                      </div>
                                    </div>
                                  </div>
                                  <div
                                    className="post_header_right hover1"
                                    // onClick={() => setShowMenu((prev) => !prev)}
                                  >
                                    <Dots color="#828387" />
                                  </div>
                                </div>
                                {report.postRef.background ? (
                                  <div
                                    className="post_bg"
                                    style={{
                                      backgroundImage: `url(${report.postRef.background})`,
                                    }}
                                  >
                                    <div className="post_bg_text">
                                      {report.postRef.text}
                                    </div>
                                  </div>
                                ) : report.postRef.type === null ||
                                  report.postRef.type === "group" ? (
                                  <>
                                    <div className="post_text">
                                      {report.postRef.text}
                                    </div>
                                    {report.postRef.images &&
                                      report.postRef.images.length && (
                                        <div
                                          className={
                                            report.postRef.images.length === 1
                                              ? "grid_1"
                                              : report.postRef.images.length ===
                                                2
                                              ? "grid_2"
                                              : report.postRef.images.length ===
                                                3
                                              ? "grid_3"
                                              : report.postRef.images.length ===
                                                4
                                              ? "grid_4"
                                              : report.postRef.images.length >=
                                                  5 && "grid_5"
                                          }
                                        >
                                          {report.postRef.images
                                            .slice(0, 5)
                                            .map((image, i) => (
                                              <img
                                                src={image.url}
                                                key={i}
                                                alt=""
                                              />

                                              // className={`img-${i}`}
                                            ))}
                                          {report.postRef.images.length > 5 && (
                                            <div className="more-pics-shadow">
                                              +
                                              {report.postRef.images.length - 5}
                                            </div>
                                          )}
                                        </div>
                                      )}
                                  </>
                                ) : report.postRef.type === "profilePicture" ? (
                                  <div className="post_profile_wrap">
                                    <div className="post_updated_bg">
                                      <img
                                        src={report.postRef.user.cover}
                                        alt=""
                                      />
                                    </div>
                                    <img
                                      src={report.postRef.images[0].url}
                                      alt=""
                                      className="post_updated_picture"
                                    />
                                  </div>
                                ) : (
                                  <img
                                    src={report.postRef.images[0].url}
                                    alt=""
                                  />
                                )}

                              </div>
                            </div>
                          </>
                        )}
                        {report.commentRef && (
                          <>
                            <div
                              className="comment"
                              style={{
                                marginBottom: "10px",
                                marginTop: "10px",
                              }}
                            >
                              <Link
                                to={`/profile/${report.commentRef.commentBy._id}`}
                              >
                                <img
                                  src={report.commentRef.commentBy.picture}
                                  alt=""
                                  className="comment_img"
                                />
                              </Link>
                              <div
                                className="comment_col"
                                style={{ display: "flex" }}
                              >
                                <div className="comment_react_wrap">
                                  <div className="comment_wrap">
                                    <Link
                                      to={`/profile/${report.commentRef.commentBy._id}`}
                                    >
                                      <div className="comment_name hover6">
                                        <b>
                                          {
                                            report.commentRef.commentBy
                                              .first_name
                                          }{" "}
                                          {
                                            report.commentRef.commentBy
                                              .last_name
                                          }
                                        </b>
                                      </div>
                                    </Link>
                                    <div className="comment_text">
                                      {report.commentRef.commentRef && (
                                        <Link
                                          to={`/profile/${report.commentRef.commentRef.commentBy._id}`}
                                          className="hover6"
                                        >
                                          <b>
                                            {
                                              report.commentRef.commentRef
                                                .commentBy.first_name
                                            }{" "}
                                            {
                                              report.commentRef.commentRef
                                                .commentBy.last_name
                                            }
                                          </b>
                                        </Link>
                                      )}{" "}
                                      {report.commentRef.comment}
                                    </div>
                                  </div>
                                </div>

                                {report.commentRef.image && (
                                  <>
                                    <div className="comment_react_wrap">
                                      <div className="img_wrap">
                                        <img
                                          src={report.commentRef.image}
                                          alt=""
                                          className="comment_image"
                                        />
                                      </div>
                                    </div>
                                  </>
                                )}
                              </div>
                            </div>
                          </>
                        )}

                        <div className="mmenu_splitter"></div>
                        <div className="menu_button_report">
                          <div
                            className="gray_btn"
                            style={{ width: "330px" }}
                            // onClick={() => functionkeepReport(report._id)}
                          >
                            <span>Keep</span>
                          </div>

                          <div
                            className="gray_btn"
                            style={{ width: "330px" }}
                            // onClick={() =>
                            //   report.commentRef
                            //     ? functionremoveReport(
                            //         report._id,
                            //         null,
                            //         report.commentRef._id
                            //       )
                            //     : functionremoveReport(
                            //         report._id,
                            //         report.postRef._id,
                            //         null
                            //       )
                            // }
                          >
                            <span>Remove</span>
                          </div>
                        </div>
                      </div>
                    ))}
                  </>
                )}
              </>
            )}
          </div>
        </div>
      </div>
    </section>
  );
};

export default ReportMain;
