import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { listGroups } from "../../Redux/Actions/GroupActions";
import Loading from "../LoadingError/Loading";
import Message from "../LoadingError/Error";

const MainGroups = () => {
  const dispatch = useDispatch();

  const groupList = useSelector((state) => state.groupList);
  const { loading, error, groups } = groupList;

  const groupDelete = useSelector((state) => state.groupDelete);
  const { error: errorDelete, success: successDelete } = groupDelete;

  useEffect(() => {
    dispatch(listGroups());
  }, [dispatch, successDelete]);

  return (
    <section className="content-main">
      <div className="content-header">
        <h2 className="content-title">Groups</h2>
        <div>
          <Link to="/addgroup" className="btn btn-primary">
            Create new
          </Link>
        </div>
      </div>

      <div className="card mb-4 shadow-sm">
        <header className="card-header bg-white ">
          <div className="row gx-3 py-3">
            <div className="col-lg-4 col-md-6 me-auto ">
              <input
                type="search"
                placeholder="Search..."
                className="form-control p-2"
              />
            </div>
            <div className="col-lg-2 col-6 col-md-3">
              <select className="form-select">
                <option>All category</option>
                <option>Electronics</option>
                <option>Clothings</option>
                <option>Something else</option>
              </select>
            </div>
            <div className="col-lg-2 col-6 col-md-3">
              <select className="form-select">
                <option>Latest added</option>
                <option>Cheap first</option>
                <option>Most viewed</option>
              </select>
            </div>
          </div>
        </header>

        <div className="card-body">
          {errorDelete && (
            <Message variant="alert-danger">{errorDelete}</Message>
          )}
          {loading ? (
            <Loading />
          ) : error ? (
            <Message variant="alert-danger">{error}</Message>
          ) : (
            <div className="row">
              {/* Groups */}
              {groups.map((group) => (
                <div className="card-body">
                  <h5 className="card-title mt-5">
                    {group.name} 
                  </h5>
                  <div className="card-text text-muted">
                    {/* {group.isAdmin === true ? (
                      <p className="m-0">Admin</p>
                    ) : (
                      <p className="m-0">User</p>
                    )}

                    <p>
                      <a href={`mailto:${group.email}`}>{group.email}</a>
                    </p> */}
                  </div>
                </div>
              ))}
            </div>
          )}

          {/* <nav className="float-end mt-4" aria-label="Page navigation">
            <ul className="pagination">
              <li className="page-item disabled">
                <Link className="page-link" to="#">
                  Previous
                </Link>
              </li>
              <li className="page-item active">
                <Link className="page-link" to="#">
                  1
                </Link>
              </li>
              <li className="page-item">
                <Link className="page-link" to="#">
                  2
                </Link>
              </li>
              <li className="page-item">
                <Link className="page-link" to="#">
                  3
                </Link>
              </li>
              <li className="page-item">
                <Link className="page-link" to="#">
                  Next
                </Link>
              </li>
            </ul>
          </nav> */}
        </div>
      </div>
    </section>
  );
};

export default MainGroups;
