import React from "react";
import { Dots, Public } from "../../svg";
import Message from "../LoadingError/Error";
import Loading from "../LoadingError/Loading";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { keepReport, removeReport } from "../function/report";
import moment from "moment";
import Orders from "../orders/Orders";
import { listReports } from "../../Redux/Actions/ReportActions";
const ReportMain = () => {
  const dispatch = useDispatch();
  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;
  const reportList = useSelector((state) => state.reportList);
  const { loading, error, reports } = reportList;
  const functionkeepReport = async (idReport) => {
    await keepReport(idReport, userInfo.token);
    dispatch(listReports());
  };
  const functionremoveReport = async (idReport, postRef, commentRef) => {
    await removeReport(idReport, postRef, commentRef, userInfo.token);
    dispatch(listReports());
  };
  return (
    <section className="content-main">
      <div className="content-header">
        <h2 className="content-title">Reports</h2>
      </div>

      <div className=" mb-4 shadow-sm">
        <header className="card-header bg-white">
          <div className="row gx-3 py-3">
            <div className="col-lg-4 col-md-6 me-auto">
              <input
                type="text"
                placeholder="Search..."
                className="form-control p-2"
              />
            </div>
            <div className="col-lg-2 col-6 col-md-3">
              <select className="form-select">
                <option>Status</option>
                <option>Active</option>
                <option>Disabled</option>
                <option>Show all</option>
              </select>
            </div>
            <div className="col-lg-2 col-6 col-md-3">
              <select className="form-select">
                <option>Show 20</option>
                <option>Show 30</option>
                <option>Show 40</option>
              </select>
            </div>
          </div>
        </header>
        <div className="card-body">
          <div
            className="table-responsive"
            style={{
              justifyContent: "center",
              display: "grid",
              flexDirection: "column",
            }}
          >
            {loading ? (
              <Loading />
            ) : error ? (
              <Message variant="alert-danger">{error}</Message>
            ) : (
              <>
                {reports.length === 0 ? (
                  <>
                    {" "}
                    <div className="No_results">
                      <p>No reports to show</p>
                    </div>
                  </>
                ) : (
                  <>
                    <table className="table">
                      <thead>
                        <tr>
                          <th scope="col">Name</th>
                          <th scope="col">Email</th>
                          <th scope="col">Total</th>
                          <th scope="col">Status</th>
                          <th scope="col">Date</th>
                          <th>Status</th>
                          <th scope="col" className="text-end">
                            Action
                          </th>
                        </tr>
                      </thead>
                      <tbody>
                        {reports.map((report) => (
                          <tr key={report?._id}>
                            <td>
                              <b>{report?.userRef?.first_name} {report?.userRef?.last_name}</b>
                            </td>
                            <td>{report?.userRef?.email}</td>
                            <td>${report?.totalPrice}</td>
                            <td>
                              {report?.isPaid ? (
                                <span className="badge rounded-pill alert-success">
                                  Paid At{" "}
                                  {moment(report.paidAt).format("MMM Do YY")}
                                </span>
                              ) : (
                                <span className="badge rounded-pill alert-danger">
                                  Not Paid
                                </span>
                              )}
                            </td>
                            <td>
                              {moment(report?.createdAt).format("MMM Do YY")}
                            </td>
                            <td>
                              {report?.isDelivered ? (
                                <span className="badge btn-success">
                                  Delivered
                                </span>
                              ) : (
                                <span className="badge btn-dark">
                                  Not delivered
                                </span>
                              )}
                            </td>
                            <td className="d-flex justify-content-end align-item-center">
                              <Link
                                to={`/order/${report?._id}`}
                                className="text-success"
                              >
                                <i className="fas fa-eye"></i>
                              </Link>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </>
                )}
              </>
            )}
          </div>
        </div>
      </div>
    </section>
  );
};

export default ReportMain;
