import React, { useEffect } from "react";
import OrderDetailProducts from "./OrderDetailProducts";
import OrderDetailInfo from "./OrderDetailInfo";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";

import {
  deliverOrder,
  getOrderDetails,
} from "../../Redux/Actions/ReportActions";
import Loading from "../LoadingError/Loading";
import Message from "../LoadingError/Error";
import moment from "moment";
import { getReport } from "../../Redux/Actions/ReportActions";
const OrderDetailmain = (props) => {
  const { id } = props;
  const dispatch = useDispatch();

  const reportDetails = useSelector((state) => state.reportDetails);
  const { loading, report } = reportDetails;

  useEffect(() => {
    dispatch(getReport(id));
  }, [dispatch, id]);
  console.log(report);
  return (
    <section className="content-main">
      <div className="content-header">
        <Link to="/reports" className="btn btn-dark text-white">
          Back To Orders
        </Link>
      </div>

      {loading ? (
        <Loading />
      ) : (
        <div className="card">
          <header className="card-header p-3 Header-green">
            <div className="row align-items-center ">
              <div className="col-lg-6 col-md-6">
                <span>
                  <i className="far fa-calendar-alt mx-2"></i>
                  <b className="text-white">
                    {moment(report?.createdAt).format("llll")}
                  </b>
                </span>
                <br />
                <small className="text-white mx-3 ">
                  Report ID: {report?._id}
                </small>
              </div>
              <div className="col-lg-6 col-md-6 ms-auto d-flex justify-content-end align-items-center">
                <select
                  className="form-select d-inline-block"
                  style={{ maxWidth: "200px" }}
                >
                  <option>Change status</option>
                  <option>Awaiting payment</option>
                  <option>Confirmed</option>
                  <option>Shipped</option>
                  <option>Delivered</option>
                </select>
                <Link className="btn btn-success ms-2" to="#">
                  <i className="fas fa-print"></i>
                </Link>
              </div>
            </div>
          </header>
          <div className="card-body">
            {/* Order info */}
            {/* <OrderDetailInfo order={report} /> */}

            <div className="row">
              <div className="col-lg-9">
                <div className="table-responsive">
                  <div className="profile_card">
                    <div>
                      <p>
                        <Link
                          to={`/profile/${report.userRef._id}`}
                          className="hover6"
                        >
                          {report.userRef.first_name} {report.userRef.last_name}{" "}
                        </Link>
                        {report.postRef && !report.commentRef && (
                          <>
                            {" "}
                            <span style={{ fontWeight: "300" }}>
                              {" "}
                              reported this post for
                            </span>
                          </>
                        )}
                        {report.commentRef && (
                          <>
                            {" "}
                            <span style={{ fontWeight: "300" }}>
                              {" "}
                              reported this comment on
                            </span>
                            <div className="hover6">
                              {" "}
                              {report.userRef.first_name}'s Post
                            </div>
                            <span style={{ fontWeight: "300" }}> for</span>
                          </>
                        )}
                        <span style={{ fontWeight: "400" }}>
                          {" "}
                          "{report.type}".
                        </span>
                      </p>
                    </div>
                    <div className="mmenu_splitter"></div>
                    {report.postRef && !report.commentRef && (
                      <>
                        <div className="posts">
                          <div className="post">
                            <div
                              className="post_header"
                              id={`post-${report.postRef._id}`}
                            >
                              <div className="post_header_left">
                                <Link
                                  to={`/profile/${report.postRef.user._id}`}
                                >
                                  <img
                                    src={report.postRef.user.picture}
                                    alt=""
                                  />
                                </Link>

                                <div className="header_col">
                                  <div className="post_profile_name">
                                    <>
                                      <Link
                                        to={`/profile/${report.postRef.user._id}`}
                                        className="hover6"
                                      >
                                        {report.postRef.user.first_name}{" "}
                                        {report.postRef.user.last_name}
                                      </Link>
                                    </>

                                    <div className="updated_p">
                                      {report.postRef.type ==
                                        "profilePicture" &&
                                        `updated ${
                                          report.postRef.user.gender === "male"
                                            ? "his"
                                            : "her"
                                        } profile picture`}
                                      {report.postRef.type == "coverPicture" &&
                                        `updated ${
                                          report.postRef.user.gender === "male"
                                            ? "his"
                                            : "her"
                                        } cover picture`}
                                      {report.postRef.type ==
                                        "coverPictureGroup" &&
                                        `updated the group cover photo.`}
                                    </div>
                                  </div>
                                  <div className="post_profile_privacy_date">
                                    <Moment fromNow interval={30}>
                                      {report.postRef.createdAt}
                                    </Moment>
                                    . <Public color="#828387" />
                                  </div>
                                </div>
                              </div>
                              <div className="post_header_right hover1">
                                <Dots color="#828387" />
                              </div>
                            </div>
                            {report.postRef.background ? (
                              <div
                                className="post_bg"
                                style={{
                                  backgroundImage: `url(${report.postRef.background})`,
                                }}
                              >
                                <div className="post_bg_text">
                                  {report.postRef.text}
                                </div>
                              </div>
                            ) : report.postRef.type === null ||
                              report.postRef.type === "group" ? (
                              <>
                                <div className="post_text">
                                  {report.postRef.text}
                                </div>
                                {report.postRef.images &&
                                  report.postRef.images.length && (
                                    <div
                                      className={
                                        report.postRef.images.length === 1
                                          ? "grid_1"
                                          : report.postRef.images.length === 2
                                          ? "grid_2"
                                          : report.postRef.images.length === 3
                                          ? "grid_3"
                                          : report.postRef.images.length === 4
                                          ? "grid_4"
                                          : report.postRef.images.length >= 5 &&
                                            "grid_5"
                                      }
                                    >
                                      {report.postRef.images
                                        .slice(0, 5)
                                        .map((image, i) => (
                                          <img src={image.url} key={i} alt="" />

                                          // className={`img-${i}`}
                                        ))}
                                      {report.postRef.images.length > 5 && (
                                        <div className="more-pics-shadow">
                                          +{report.postRef.images.length - 5}
                                        </div>
                                      )}
                                    </div>
                                  )}
                              </>
                            ) : report.postRef.type === "profilePicture" ? (
                              <div className="post_profile_wrap">
                                <div className="post_updated_bg">
                                  <img src={report.postRef.user.cover} alt="" />
                                </div>
                                <img
                                  src={report.postRef.images[0].url}
                                  alt=""
                                  className="post_updated_picture"
                                />
                              </div>
                            ) : (
                              <div
                                className="post_cover_wrap"
                                style={{ cursor: "pointer" }}
                              >
                                <img
                                  src={report.postRef.images[0].url}
                                  alt=""
                                />
                              </div>
                            )}
                          </div>
                        </div>
                      </>
                    )}
                    {report.commentRef && (
                      <>
                        <div
                          className="comment"
                          style={{
                            marginBottom: "10px",
                            marginTop: "10px",
                          }}
                        >
                          <Link
                            to={`/profile/${report.commentRef.commentBy._id}`}
                          >
                            <img
                              src={report.commentRef.commentBy.picture}
                              alt=""
                              className="comment_img"
                            />
                          </Link>
                          <div className="comment_col">
                            <div className="comment_react_wrap">
                              <div className="comment_wrap">
                                <Link
                                  to={`/profile/${report.commentRef.commentBy._id}`}
                                >
                                  <div className="comment_name hover6">
                                    <b>
                                      {report.commentRef.commentBy.first_name}{" "}
                                      {report.commentRef.commentBy.last_name}
                                    </b>
                                  </div>
                                </Link>
                                <div className="comment_text">
                                  {report.commentRef.commentRef && (
                                    <Link
                                      to={`/profile/${report.commentRef.commentRef.commentBy._id}`}
                                      className="hover6"
                                    >
                                      <b>
                                        {
                                          report.commentRef.commentRef.commentBy
                                            .first_name
                                        }{" "}
                                        {
                                          report.commentRef.commentRef.commentBy
                                            .last_name
                                        }
                                      </b>
                                    </Link>
                                  )}{" "}
                                  {report.commentRef.comment}
                                </div>
                              </div>

                              {/* {!report.commentRef.image && (
                            <div className="reacts_count_comment">
                              <div className="reacts_count_imgs">
                                {reacts &&
                                  reacts
                                    .sort((a, b) => {
                                      return b.count - a.count;
                                    })
                                    .slice(0, 3)
                                    .map(
                                      (react, i) =>
                                        react.count > 0 && (
                                          <img
                                            style={{
                                              position: "relative",
                                              right: `${i * 2}px`,
                                            }}
                                            src={`../../../reacts/${react.react}.svg`}
                                            alt=""
                                            key={i}
                                            onClick={() =>
                                              setVisibleReactComment(
                                                comment._id,
                                                user.token
                                              )
                                            }
                                          />
                                        )
                                    )}
                              </div>
                              <div className="reacts_count_num">
                                {total > 1 && total}
                              </div>
                            </div>
                          )} */}
                            </div>

                            {report.commentRef.image && (
                              <>
                                <div className="comment_react_wrap">
                                  <div className="img_wrap">
                                    <img
                                      src={report.commentRef.image}
                                      alt=""
                                      className="comment_image"
                                    />
                                  </div>
                                </div>
                              </>
                            )}
                          </div>
                        </div>
                      </>
                    )}

                    <div className="mmenu_splitter"></div>
                    <div className="menu_button_report">
                      <div
                        className="gray_btn"
                        style={{ width: "330px" }}
                        onClick={() => functionkeepReport(report._id)}
                      >
                        <span>Keep</span>
                      </div>

                      <div
                        className="gray_btn"
                        style={{ width: "330px" }}
                        onClick={() =>
                          report.commentRef
                            ? functionremoveReport(
                                report._id,
                                null,
                                report.commentRef._id
                              )
                            : functionremoveReport(
                                report._id,
                                report.postRef._id,
                                null
                              )
                        }
                      >
                        <span>Remove</span>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
              <div className="col-lg-3">
                <div className="box shadow-sm bg-light">
                  {report?.isDelivered ? (
                    <button className="btn btn-success col-12">
                      DELIVERED AT ({" "}
                      {moment(report?.isDeliveredAt).format("MMM Do YY")})
                    </button>
                  ) : (
                    <>
                      {/* {loadingDelivered && <Loading />} */}
                      <button
                        // onClick={deliverHandler}
                        className="btn btn-dark col-12"
                      >
                        MARK AS DELIVERED
                      </button>
                    </>
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </section>
  );
};

export default OrderDetailmain;
